/******************************************************************************
** File: cfarVerify.c
**
** HPEC Challenge Benchmark Suite
** CFAR Kernel Benchmark
**
** Contents:
**  The verifier for the CFAR kernel benchmark.  Checks the actual 
**  target list against the list of targets found by the CFAR benchmark.
**
** Input files:
**  data/<id>-cfar-actual.dat - the actual placed targets (generated 
**                               by cfarGenerator()).
**  data/<id>-cfar-detect.dat - the targets found (generated by cfar).
**
** Author: Edmund L. Wong 
**         MIT Lincoln Laboratory
**
******************************************************************************/

#include <stdlib.h>
#include <stdio.h>
#include <PcaCArray.h>

/* useful typedef's */
typedef unsigned int uint;

int main(int argc, char** argv)
{
  /* files containing the actual targets and detected ones */
  struct PcaCArrayInt actual, detect;
  char *m_actualfile, *m_detectfile;
  int** pp_actual, **pp_detect;

  /* used to keep track of which actual targets were already used */
  uint* m_matched;

  /* various counters */
  uint i_a = 0, i_r = 0, num_errors = 0;

  /* check arguments */
  if (argc != 2) {
    fprintf(stderr, "Usage: %s [dataset id]\n", argv[0]);
    exit(-1);
  }

#ifdef VERBOSE
  /* print informative message */
  printf("[Verifying dataset %s]\n", argv[1]);
#endif
  
  /* load up files */
  m_actualfile = (char*) malloc(strlen("data/") + strlen(argv[1]) + 
				strlen("-cfar-actual.dat") + 1);
  m_detectfile = (char*) malloc(strlen("data/") + strlen(argv[1]) + 
				strlen("-cfar-detect.dat") + 1);
  sprintf(m_actualfile, "data/%s-cfar-actual.dat", argv[1]);
  sprintf(m_detectfile, "data/%s-cfar-detect.dat", argv[1]);
  readFromFile(int, m_actualfile, actual);
  readFromFile(int, m_detectfile, detect);
  pp_actual = (int**) actual.datav;
  pp_detect = (int**) detect.datav;
  m_matched = (uint*) calloc(actual.size[0], sizeof(uint));
  free(m_actualfile);
  free(m_detectfile);
  
  /* Try to find each target from results in the actual target list.
   * This can be done more intelligently, but since the lists should 
   * be relatively short, this approach is sufficient.  Note we
   * have to adjust indices since the C version uses zero-indexing
   * whereas matlab uses one-indexing. */
  for (i_r = 0; i_r < detect.size[0]; i_r++) {
    for (i_a = 0; i_a < actual.size[0]; i_a++) {
      if (pp_actual[i_a][0] == pp_detect[i_r][0]+1 &&
	  pp_actual[i_a][1] == pp_detect[i_r][1]+1 &&
	  pp_actual[i_a][2] == pp_detect[i_r][2]+1 &&
	  m_matched[i_a] == 0)
      {
	m_matched[i_a] = 1;
	break;
      }
    }
    /* check to see if we've matched any target */
    if (i_a >= actual.size[0]) {
#ifdef VERBOSE
      printf("Unknown target: (%d, %d, %d)\n", 
	     pp_detect[i_r][0]+1, pp_detect[i_r][1]+1, pp_detect[i_r][2]+1);
#endif
      num_errors++;
    }
  }
  
  /* check if any targets remain in actual list */
  for (i_a = 0; i_a < actual.size[0]; i_a++) {
    if (m_matched[i_a] == 0) {
#ifdef VERBOSE
      printf("Missed target:  (%d, %d, %d)\n", 
	     pp_actual[i_a][0], pp_actual[i_a][1], pp_actual[i_a][2]);
#endif
      num_errors++;
    }
  }
  
  /* print out message and clean up */
#ifdef VERBOSE
  printf("Number of differences from actual target list: %d\n", num_errors);
#endif
  printf("Verification: ");
  if (num_errors == 0) printf("PASS\n");
  else printf("FAIL\n");
  
  /* clean up memory */
  clean_mem(int, actual);
  clean_mem(int, detect);
  free(m_matched);
  return 0;
}
